import React from 'react';
import axios from 'axios';
import { useState,useEffect } from 'react/cjs/react.development';

const Countries = () => {
    const[data,setData] = useState([]);
    const url = "https://restcountries.com/v3.1/all?fields=flags,name,population,capital,region";
    const [getApi,setGetApi] = useState(true);
    const[sortedData,setSortedData] = useState();

    useEffect(() => {
        if(getApi){
            axios
        .get(url)
        .then(res =>setData(res.data))
        setGetApi(false)
        };
        const longData = data.sort((a,b)=>{
            return b.population - a.population
        })
        longData.lenght=10
        setSortedData(longData)
        // const dataConverti= ()=>{
        //     const datObject = Object.keys(data).map((i)=>data[i]);
        //     const arr = datObject
        // }
        // dataConverti()
    }, [data,getApi]);

    return (
        <div>
            <ul>
                {sortedData.map((country)=>(
                    <li>{country.name.common}</li>
                ))}
            </ul>
        </div>
    );
}

export default Countries;
