import React from 'react';
import axios from 'axios';
import{useState,useEffect} from "react"
import Card from './Card';

const Countries = () => {
    const[data,setData]=useState([]);
    const url = "https://restcountries.com/v3.1/all?fields=flags,name,population,capital,region";
    const [sortedData,setSortedData] = useState([]);
    const [getApi,setGetApi] = useState(true)
    const [rangeValue,setRangeValue] = useState(10)

    useEffect(() => {
        if(getApi){
            axios
            .get(url)
            .then(res=>{setData(res.data)});
            setGetApi(false)
        }

        const sortedCount = ()=>{
            const sortedOb = Object.keys(data).map((i)=>data[i]);
            const sortedArr = sortedOb.sort((a,b)=>{
                return b.population -a.population
            })
            // sortedArr.length={rangeValue}
            setSortedData(sortedArr)
        }
        sortedCount()
    },[data,getApi,rangeValue]);
    return (
        <div className="countries">
            <div className="sortcont">
                <input type="range" min="1" max="250" value={rangeValue}
                    onChange={(e)=>setRangeValue(e.target.value)}
                 />
            </div>
            <ul>
                {sortedData.map(country=>(
                    <Card country ={country} key={country.name.official}/>
                ))}
            </ul>
        </div>
    );
};

export default Countries;
