import React from 'react';
import axios from 'axios';
import{useState,useEffect} from "react"
import Card from './Card';

const Countries = () => {
    const[data,setData]=useState([]);
    const url = "https://restcountries.com/v3.1/all?fields=flags,name,population,capital,region";
    const [sortedData,setSortedData] = useState([])

    useEffect(() => {
        axios
        .get(url)
        .then(res=>{setData(res.data)});

        const sortedCount = ()=>{
            const sorteOb = Object.keys(data).map((i)=>data[i]);
            const sortedArr = sorteOb.sort((a,b)=>{
                return b.population -a.population
            })
            console.log(sortedArr);
        }
        sortedCount()
    },[data]);
    return (
        <div className="countries">
            <ul>
                {data.map(country=>(
                    <Card country ={country} key={country.name.official}/>
                ))}
            </ul>
        </div>
    );
};

export default Countries;
